import { useReducer } from "react";

export default function RegistrationForm() {

    const init = {
        user_name: {value:0,valid: false, touched: false, error:""},
        password: {value:"",valid: false, touched: false, error:""},
        contact: {value:0,valid: false, touched: false, error:""},
        email: {value:0,valid: false, touched: false, error:""},
        formValid: false
    }

    const reducer = (state,action) => {
        switch(action.type)
        {
            case 'update':
                //object destructuring
                const {key,value,touched,valid,error,formValid} = action.data;
                return {...state,[key]:{value,touched,valid,error},formValid}
            case 'reset':
                return init;        
        }
    }

    const[emp,dispatch] = useReducer(reducer,init)

    const validateData = (key,val) => {
        let valid = true;
        let error = ""
        switch(key)
        {
            case 'user_name':
               var pattern = /^[0-9]{4}$/ 
               if(!pattern.test(val))
               {
                  valid = false;
                  error = "empno should be 4 digits only"
               }

               break;
            case 'password': 
                var pattern = /^[0-9]{4}$/ 
                if(!pattern.test(val))
                {
                    valid = false;
               error = "empno should be 4 digits only"
            }

            break;


        }
        return { valid: valid, error: error}
    }

    
    const handleChange = (key,value) => {
        //1. call validateData function
        const {valid, error} = validateData(key,value);

        //2. check the validity status of the form
        let formValid = true;
        for(let k in emp)
        {
            //console.log(emp[k].valid)
            if(emp[k].valid === false)
            {
                formValid = false;
                break;
            }
        }
        console.log(formValid);

        //3. call to dispatch - updating the state
        dispatch({type: "update",data:{key,value,touched:true,valid,error,formValid}})
    }

    const submitData = (e) =>{
        e.preventDefault();
        //server side API
    }


    return (
        <div>
            <h1> Emp Form </h1>
            <form>
                empno : <input type="number" name="empno" 
                value={emp.empno.value}
                onChange={(e)=>{handleChange("empno",e.target.value)}} 
                onBlur={(e)=>{handleChange("empno",e.target.value)}} />
                <br/>
                <div style={{ display: emp.empno.touched && !emp.empno.valid  ?"block":"none"}}>
                    { emp.empno.error}
                </div>


                Ename : <input type="text" name="ename" 
                value={emp.ename.value}
                onChange={(e)=>{handleChange("ename",e.target.value)}} 
                onBlur={(e)=>{handleChange("ename",e.target.value)}} />
                <br/>

                sal : <input type="number" name="sal" 
                value={emp.sal.value}
                onChange={(e)=>{handleChange("sal",e.target.value)}} 
                onBlur={(e)=>{handleChange("sal",e.target.value)}} />
                <br/>

                deptno : <input type="number" name="deptno" 
                value={emp.deptno.value}
                onChange={(e)=>{handleChange("deptno",e.target.value)}} 
                onBlur={(e)=>{handleChange("deptno",e.target.value)}} />
                <br/>

                <input type="submit" value="Insert"
                disabled={!emp.formValid} 
                onClick={(e)=>{submitData(e)}} />

                <input type="reset" value="Clear" 
                onClick={()=>{dispatch({type:"reset"})}}/>
            </form>
            <p> {JSON.stringify(emp)} </p>
            
        </div>
    )



}
